package pro;

import java.util.ArrayList;
import java.util.List;

/**
 * pro.Instance.java
 * Klasa instancji.
 *
 * @author Kamil Blanik
 * @since 07-03-2019
 */
public class Instance {
    /**
     * Parametry plecaka, okreslaja jego wlasciwosci
     */
    //private String forMemory;
    private int backpackSize;
    private List<Item> itemList;

    /**
     * Konstruktor bezparamtrowy
     */
    public Instance() {
        this.backpackSize = 0;
        this.itemList = new ArrayList<>();
        //this.forMemory = "";
    }

    /**
     * Konstruktor z parametrem okreslajacym wielkosc plecaka
     *
     * @param size {@code int} parametr okreslajacy pojemnosc plecaka
     */
    public Instance(int size) {
        this.backpackSize = size;
        this.itemList = new ArrayList<>();
        //this.forMemory = "";
    }

    /**
     * Konstruktor z dwoma parametrami, pozwala okreslic poczatkowe wartosci plecaka
     *
     * @param size     parametr {@code int} okreslajacy pojemnosc plecaka
     * @param itemList parametr {@code List<pro.Item>} okreslajacy poczatkowa zawartosc plecaka
     */
    public Instance(int size, List<Item> itemList) {
        this.backpackSize = size;
        this.itemList = itemList;
        //this.forMemory = "";
    }

    /**
     * Setter wielkosci plecaka
     *
     * @param value wartosc {@code int} okreslajaca wielkosc plecaka
     */
    public void setBackpackSize(int value) {
        this.backpackSize = value;
    }

    /**
     * Getter pozyskujacy wielkosc plecaka
     *
     * @return {@code int} wielkosc plecaka
     */
    public int getBackpackSize() {
        return this.backpackSize;
    }

    /**
     * Setter przypisujacy liste przedmiotow
     *
     * @param items {@code List<pro.Item>} lista przedmiotow
     */
    public void setItemList(List<Item> items) {
        this.itemList = items;
    }

    /**
     * Getter pozyskujacy posiadane przedmioty przez plecak
     *
     * @return {@code List<pro.Item>} lista przedmiotow
     */
    public List<Item> getItemList() {
        return this.itemList;
    }

    /**
     * funkcja dodajaca przedmiot do listy przedmiotow plecaka
     *
     * @param item {@code pro.Item} dodawany przedmiot
     */
    public void addItem(Item item) {
        this.itemList.add(item);
    }

    /**
     * funkcja obliczajaca i zwracajaca obecna wage plecaka
     *
     * @return {@code int} obecna waga plecaka
     */
    public int getCurrentWeight() {
        int weight = 0;
        for (Item item : itemList)
            weight += item.getItemWeight();
        return weight;
    }

    /**
     * funkcja obliczajaca i zwracajaca obecna wartosc plecaka
     *
     * @return {@code float} obecna wartosc plecaka
     */
    public float getCurrentValue() {
        float value = 0;
        for (Item item : itemList)
            value += item.getItemValue();
        return value;
    }
}
