package pro;

import java.io.Serializable;
import java.util.Comparator;

/**
 * pro.Item.java
 * Klasa przedmiotu.
 *
 * @author Kamil Blanik
 * @since 07-03-2019
 */
public class Item implements Serializable {
    /**
     * Parametry przemiotu. Sa niezbedne do okreslnia wartosci danego przedmiotu oraz jego wagi.
     * Dzieki nim mozna klasyfikowac przedmoitu jako bardziej i mniej cenne.
     */
   // private String forMemory;
    private float itemValue;
    private int itemWeight;
    /**
     * Konstruktor z parametrami
     *
     * @param value  parametr {@code float} okreslajacy wartosc przedmiotu podczas jego tworzenia
     * @param weight parametr {@code int} okreslajacy wage przedmiotu podczas jego tworzenia
     */
    public Item(float value, int weight) {
        this.itemValue = value;
        this.itemWeight = weight;
        //this.forMemory = "";

    }
    /**
     * Konstruktor bezparametrowy.
     * Przypisuje parametra przedmiotu wartosci zerowowe
     */
    public Item() {
        this.itemValue = 0;
        this.itemWeight = 0;
        //this.forMemory = "";

    }



    /**
     * Komparator, porownuje dwa przedmioty ze wzgledu stosunek ich parametrow
     */
    public static Comparator<Item> byRatio() {
        return (i1, i2) -> Double.compare(i2.getItemRatio(), i1.getItemRatio());
    }

    /**
     * Setter przypisujacy wartosc przedmiotu
     *
     * @param value {@code float} wartosc przedmiotu
     */
    public void setItemValue(float value) {
        this.itemValue = value;
    }

    /**
     * Getter pozyskujacy wartosc przedmiotu
     *
     * @return {@code float} wartosc przedmiotu
     */
    public float getItemValue() {
        return this.itemValue;
    }

    /**
     * Setter przypisujacy wage przedmiotowi
     *
     * @param weight {@code int} waga przedmotu
     */
    public void setItemWeight(int weight) {
        this.itemWeight = weight;
    }

    /**
     * Getter pozyskujacy wage przedmiotu
     *
     * @return {@code int} waga przedmotu
     */
    public int getItemWeight() {
        return this.itemWeight;
    }

    /**
     * Metoda obliczajaca stosunek wartosc/waga przedmiotu
     *
     * @return {@code float} stosunk wartosc/waga
     */
    public float getItemRatio() {
        return itemValue / itemWeight;
    }

}
